import React from "react";
import PropTypes from "prop-types";
import {Router, Link} from "react-router-dom";
import styles from "./styles.module.scss";
import Modal from 'react-modal'
const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};

const TopNav = (props, context) => (
    <div className={styles.TopNav}>
        <div className={styles.item}>
            <div className={styles.logobox}>
                <Link to="/" className={styles.link}>
                    <div className={styles.logo}>
                        BITDIARY
                    </div>
                </Link>
            </div>
            <nav>
                <a class="" title="코인동향" href="/trends">공지사항</a>
                <a class="" title="거래소" href="/exchange">매매요약</a>
                <a class="" title="입출금" href="/balances">매매일지</a>
                <a class="on" title="투자내역" href="/investments/balance">커뮤니티</a>
            </nav>
              <div onClick={props.handleOpenModal} className={styles.loginButton}>
                로그인
              </div>
            <Modal
              isOpen={props.modalIsOpen}
              onRequestClose={props.handleCloseModal}
              className={styles.mymodal}
              overlayClassName={styles.myoverlay}

            >
              <div className={styles.modal}>
                <div className={styles.logo}>
                  BITDIARY
                </div>
                <div className={styles.menu}>
                  <button className={styles.abled} onClick={props.changeAction}>
                    로그인
                  </button>
                  <button className={styles.disabled} onClick={props.changeAction}>
                    회원가입
                  </button>
                  
                  <span className={styles.line}></span>
                </div>
                <div className={styles.login}>
                  <input type="text" name="email" maxlength="16"  placeholder="id" class="input-material"/>
                  <input type="password" name="password" maxlength="16" placeholder="pw" class="input-material"></input>
                </div>
                <button className={styles.loginButton}>
                  로그인
                </button>
              </div>
              
            </Modal>
        </div>
    </div>
);

TopNav.propTypes = {
    modalIsOpen : PropTypes.bool.isRequired,
    handleOpenModal: PropTypes.func.isRequired,
    handleCloseModal: PropTypes.func.isRequired,
    changeAction : PropTypes.func.isRequired,
    action : PropTypes.bool.isRequired,
};

TopNav.contextTypes = {
    t: PropTypes.func.isRequired
};

export default TopNav;
