import React, { Component } from "react";
import PropTypes from "prop-types";
import Main from "./presenter";
class Container extends Component {

  state = {
      loading:true,
      cn : "",
      pn : "",
      repair : null,
  };
  static propTypes = {
    find : PropTypes.func.isRequired,
  };
  _handleInputChange = event => {
    const { target: { value, name } } = event;
    this.setState({
      [name]: value
    });
    console.log(name,value);
  };

  _handleSubmit = event => {
    const { cn, pn} = this.state;
    const { find } = this.props;
    event.preventDefault();
    find(cn, pn);
   
    
  };
  componentWillReceiveProps = nextProps => {
    if (nextProps.repair) {
      this.setState({
        repair: nextProps.repair,
        loading:false 
      });
    };
  };
  

  componentDidMount() {
  };

  onKeyPress = (e) =>{
    this.setState({
      text : e.target.value
    })
    if(e.key==='Enter'){
      window.location.href="http://127.0.0.1:3000/summoner/"+e.target.value;
    }
  }
  onClick = (e) =>{
    const {text} = this.state;
      window.location.href="http://127.0.0.1:3000/summoner/"+text;
  }
  render() {
    
    return (
      <Main {...this.state} handleSubmit={this._handleSubmit} handleInputChange={this._handleInputChange}onKeyPress={this.onKeyPress}  onClick={this.onClick} />
      
    );
    
  }
}

export default Container;
