import React, { Component } from "react";
import Footer from "./presenter";
import $ from "jquery";
class Container extends Component {
  constructor() {
    super();
      this.state = {
        first : false,
        second : false,
        third : false,
        scrollTop: 0,
        scroll: false,
      }
  }

  update(){
    setTimeout( () => {
      this.setState( prevState => ({
        first: true
      }));
    }, 2000);
    
  }
  update2(){
    setTimeout( () => {
      this.setState( prevState => ({
        second: true
      }));
    }, 2000);

  }
  componentDidMount() {
    window.addEventListener('scroll', this.onScroll);

    this.update()
    this.update2()
  }
  
  componentWillUnmount = () => {
    window.removeEventListener('scroll', this.handleScroll);
  }




  onScroll = (e) => {
    const scrollTop = ('scroll', e.srcElement.scrollingElement.scrollTop);
      this.setState({
        scrollTop
      })
      var top = $("#middle").offset().top;
      if(this.state.scrollTop>2400 && this.state.scrollTop<3200){
        if(!this.state.scroll){
          this.setState({
            scroll: true
          });
        }
      }
      else{
          if(this.state.scroll){
            this.setState({
              scroll: false
            });
        }
      }
  };
  render() {
    return <Footer {...this.state} {...this.props} />;
  }
}

export default Container;
