import React, { Component } from "react";
import PropTypes from "prop-types";
import Main from "./presenter";
class Container extends Component {

  state = {
      loading:true,
      cn : "",
      pn : "",
  };
  static propTypes = {
    getSummary : PropTypes.func.isRequired,
    getCoinbiz : PropTypes.func.isRequired,
  };
  _handleInputChange = event => {
    const { target: { value, name } } = event;
    this.setState({
      [name]: value
    });
    console.log([name],value);
  };
  componentWillReceiveProps = nextProps => {
    if (nextProps.summary) {
      this.setState({
        summary: nextProps.summary,
        loading:false 
      });
    };
    if (nextProps.coinbiz) {
      console.log(nextProps.coinbiz)
      this.setState({
        series : [
          {
            data : nextProps.coinbiz
          }
        ]
    })
    
    }
  };
  

  componentDidMount() {
    const {getSummary,getCoinbiz} = this.props;
    console.log("Dd");
    getSummary();
    getCoinbiz();
    console.log(this.props);
};

  onKeyPress = (e) =>{
    this.setState({
      text : e.target.value
    })
    if(e.key==='Enter'){
      window.location.href="http://127.0.0.1:3000/summoner/"+e.target.value;
    }
  }
  onClick = (e) =>{
    const {text} = this.state;
      window.location.href="http://127.0.0.1:3000/summoner/"+text;
  }
  render() {
    
    return (
      <Main {...this.state} handleInputChange={this._handleInputChange}onKeyPress={this.onKeyPress}  onClick={this.onClick} />
      
    );
    
  }
}

export default Container;
