import React, { Component } from "react";
import PropTypes from "prop-types";
import TopNav from "./presenter";

class Container extends Component {
  state = {
    modalIsOpen : false,
    action : "login",
    username : "",
    password : "",
    err : "",
    warning : false
  };
  static propTypes = {
    usernameLogin: PropTypes.func.isRequired,
    createAccount: PropTypes.func.isRequired,
    logOut : PropTypes.func.isRequired,
    isLoggedIn : PropTypes.bool.isRequired,
    token :  PropTypes.string.isRequired,
  };
  handlelogOut = (e) =>{
    const {logOut} = this.props;
    logOut();
  }
  handleOpenModal = (e) =>{
    this.setState({ modalIsOpen: true });
  }
  
  handleCloseModal = (e) =>{
    this.setState({ modalIsOpen: false });
  }
  _handleInputChange = event => {
    const { target: { value, name } } = event;
    this.setState({
      [name]: value
    });
  };
  _handleSubmit = event => {
    const { username, password, action} = this.state;
    const { usernameLogin } = this.props;
    const { createAccount } = this.props;
    event.preventDefault();
    if (action === "login") {
      usernameLogin(username, password);
    } else if (action === "signup") {
      createAccount(username, password);
    }
    
  };
  componentWillReceiveProps = nextProps => {
    if (nextProps.err) {
      this.setState({
        warning: true,
        err : nextProps.err
      });
    }
    if (nextProps.token) {
      this.setState({
        modalIsOpen: false,
      });
    }
  };
  _changeAction = (e) => {
    this.setState(prevState => {
      const { action } = prevState;
      if (action === "login") {
        return {
          action: "signup"
        };
      } else if (action === "signup") {
        return {
          action: "login"
        };
      }
    });
  };
  render() {
    const {isLoggedIn} = this.props;
    return (
      <TopNav isLoggedIn={isLoggedIn} {...this.state} handleInputChange={this._handleInputChange} handleSubmit={this._handleSubmit} onKeyPress={this.onKeyPress}  changeAction={this._changeAction}  handleOpenModal={this.handleOpenModal}  handleCloseModal={this.handleCloseModal} onClick={this.onClick} />
    );
  }

}

export default Container;
