import React, { Component } from "react";
import PropTypes from "prop-types";
import TopNav from "./presenter";

class Container extends Component {
  state = {
    modalIsOpen : false,
    action : "login",
    username : "",
    password : ""
  };
  static propTypes = {
    usernameLogin: PropTypes.func.isRequired,
    createAccount: PropTypes.func.isRequired
  };
  handleOpenModal = (e) =>{
    this.setState({ modalIsOpen: true });
  }
  
  handleCloseModal = (e) =>{
    this.setState({ modalIsOpen: false });
  }
  _handleInputChange = event => {
    const { target: { value, name } } = event;
    this.setState({
      [name]: value
    });
  };
  _handleSubmit = event => {
    const { username, password, email, name } = this.state;
    const { createAccount } = this.props;
    event.preventDefault();
    createAccount(username, password, email, name);
  };
  _changeAction = (e) => {
    this.setState(prevState => {
      const { action } = prevState;
      if (action === "login") {
        return {
          action: "signup"
        };
      } else if (action === "signup") {
        return {
          action: "login"
        };
      }
    });
  };
  render() {
    return (
      <TopNav {...this.state} onKeyPress={this.onKeyPress}  changeAction={this._changeAction}  handleOpenModal={this.handleOpenModal}  handleCloseModal={this.handleCloseModal} onClick={this.onClick} />
    );
  }

}

export default Container;
