const SET_MMR = "SET_MMR";
function setMMR(mmr) {
  return {
    type: SET_MMR,
    mmr
  };
}
// API Actions
function getMMR(summoner_name) {
  return (dispatch, getState) => {
    fetch("/mmr/getmmr/?summoner_name="+summoner_name, {
    })
      .then(response => {
        return response.json();
      })
      .then(json => {
        
        dispatch(setMMR(json));
      })
      .catch(err => console.log(err));
  };
}
// Initial State

const initialState = {};

// Reducer

function reducer(state = initialState, action) {
  switch (action.type) {
    case SET_MMR:
      return applySetMMR(state, action);
    default:
      return state;
  }
}
// Reducer Functions

function applySetMMR(state, action) {
  const { mmr } = action;
  return {
    ...state,
    mmr
  };
}
const actionCreators = {
  getMMR,
};

export { actionCreators };

// Export reducer by default

export default reducer;
