import React, { Component } from "react";
import PropTypes from "prop-types";
import Main from "./presenter";
class Container extends Component {

  state = {
      loading:true,
      cn : "",
      pn : "",
      data : null,
      warning : false,
      image : [],
  };
  static propTypes = {
    find : PropTypes.func.isRequired,
  };
  _handleInputChange = event => {
    const { target: { value, name } } = event;
    this.setState({
      [name]: value
    });
    console.log(name,value);
  };
  selectOnChange = event => {
    
    const { target: { value } } = event;
    changeDate(value);
  };
  changeDate(index){
    const { data } = this.state;
    var arr = []
    
    for(var i=0;i<data[index].repairImg.length;i++){
      arr.push({url: data[index].repairImg[i].img_url})
    }
    this.setState({
      image: arr
    });
    console.log(arr);
  };
  _handleSubmit = event => {
    const { cn, pn} = this.state;
    const { find } = this.props;
    event.preventDefault();
    find(cn, pn);
    console.log("dd")
    
  };
  componentWillReceiveProps = nextProps => {
    console.log(nextProps.repair)
    if (nextProps.repair) {
      this.setState({
        data: nextProps.repair,
        loading:false 
      });
      var arr = [];
      for(var i=0;i<nextProps.repair[0].repairImg.length;i++){
        arr.push({url: nextProps.repair[0].repairImg[i].img_url})
      }
      this.setState({
        image: arr
      });
      console.log(arr);
    };
    if (nextProps.err) {
      console.log(nextProps.err);
      this.setState({
        warning: true,
      });
    };
  };
  

  componentDidMount() {
  };

  onKeyPress = (e) =>{
    this.setState({
      text : e.target.value
    })
    if(e.key==='Enter'){
      window.location.href="http://127.0.0.1:3000/summoner/"+e.target.value;
    }
  }
  onClick = (e) =>{
    const {text} = this.state;
      window.location.href="http://127.0.0.1:3000/summoner/"+text;
  }
  render() {
    
    return (
      <Main {...this.state} selectOnChange={this.selectOnChange} handleSubmit={this._handleSubmit} handleInputChange={this._handleInputChange}onKeyPress={this.onKeyPress}  onClick={this.onClick} />
      
    );
    
  }
}

export default Container;
