import React, { Component } from "react";
import PropTypes from "prop-types";
import Main from "./presenter";
class Container extends Component {

  state = {
      loading:true,
      summary : null,
      series: [
        {
          data: [
            {
              x: 'INTC',
              y: 1.2
            },
            {
              x: 'GS',
              y: 0.4
            },
            {
              x: 'CVX',
              y: -1.4
            },
            
          ]
        }
      ],
      options: {
        legend: {
          show: false
        },
        chart: {
          height: 350,
          type: 'treemap'
        },
        title: {
          text: '코인별 손익 Top 20을 가시적으로 보여줍니다.',
          style: {
            fontFamily: 'GONGLI',
            paddingTop: '10px',
            fontSize: '15px',
          },
        },
        dataLabels: {
          enabled: true,
          style: {
            fontFamily: 'GONGLI',
            fontSize: '10px',
          },
          formatter: function(text, op) {
            return [text, op.value]
          },
          offsetY: -4
        },
        plotOptions: {
          treemap: {
            enableShades: true,
            shadeIntensity: 0.3,
            reverseNegativeShade: true,
            colorScale: {
              ranges: [
                {
                  from: -600000000,
                  to: 0,
                  color: '#BC5D7B'
                },
                {
                  from: 0.001,
                  to: 60000000,
                  color: '#001dff'
                }
              ]
            }
          }
        }
      },
  };
  static propTypes = {
    getSummary : PropTypes.func.isRequired,
    getCoinbiz : PropTypes.func.isRequired,
  };
  
  componentWillReceiveProps = nextProps => {
    if (nextProps.summary) {
      this.setState({
        summary: nextProps.summary,
        loading:false 
      });
    };
    if (nextProps.coinbiz) {
      console.log(nextProps.coinbiz)
      this.setState({
        series : [
          {
            data : nextProps.coinbiz
          }
        ]
    })
    
    }
  };
  

  componentDidMount() {
    const {getSummary,getCoinbiz} = this.props;
    console.log("Dd");
    getSummary();
    getCoinbiz();
    console.log(this.props);
};

  onKeyPress = (e) =>{
    this.setState({
      text : e.target.value
    })
    if(e.key==='Enter'){
      window.location.href="http://127.0.0.1:3000/summoner/"+e.target.value;
    }
  }
  onClick = (e) =>{
    const {text} = this.state;
      window.location.href="http://127.0.0.1:3000/summoner/"+text;
  }
  render() {
    
    return (
      <Main {...this.state} onKeyPress={this.onKeyPress}  onClick={this.onClick} />
      
    );
    
  }
}

export default Container;
