import React, { Component } from "react";
import PropTypes from "prop-types";
import MTopNav from "./presenter";

class Container extends Component {
  state = {
    text : ""
  };
  onKeyPress = (e) =>{
    this.setState({
      text : e.target.value
    })

    if(e.key==='Enter'){
      window.location.href="http://127.0.0.1:3000/summoner/"+e.target.value;
    }
  }
  onClick = (e) =>{

    const {text} = this.state;
    window.location.href="http://127.0.0.1:3000/summoner/"+text;
  }
  render() {
    return (
      <MTopNav onKeyPress={this.onKeyPress} onClick={this.onClick} />
    );
  }

}

export default Container;
