const SET_MMR = "SET_MMR";
const SET_MMRDATE = "SET_MMRDATE";
function setMMR(mmr) {
  return {
    type: SET_MMR,
    mmr
  };
}
function setMMRDATE(mmrdate) {
  return {
    type: SET_MMRDATE,
    mmrdate
  };
}
// API Actions
function getMMR(summoner_name) {
  return (dispatch, getState) => {
    fetch("/mmr/getmmr/?summoner_name="+summoner_name, {
    })
      .then(response => {
        return response.json();
      })
      .then(json => {
        
        dispatch(setMMR(json));
      })
      .catch(err => console.log(err));
  };
}
function getMMRDate(summoner_name) {
  return (dispatch, getState) => {
    fetch("/mmr/getmmrdate/?summoner_name="+summoner_name, {
    })
      .then(response => {
        return response.json();
      })
      .then(json => {
        
        dispatch(setMMRDATE(json));
      })
      .catch(err => console.log(err));
  };
}
// Initial State

const initialState = {};

// Reducer

function reducer(state = initialState, action) {
  switch (action.type) {
    case SET_MMR:
      return applySetMMR(state, action);
    case SET_MMRDATE:
      return applySetMMRDATE(state, action);
    default:
      return state;
  }
}
// Reducer Functions

function applySetMMR(state, action) {
  const { mmr } = action;
  return {
    ...state,
    mmr
  };
}
function applySetMMRDATE(state, action) {
  const { mmrdate } = action;
  return {
    ...state,
    mmrdate
  };
}
const actionCreators = {
  getMMR,
  getMMRDate,
};

export { actionCreators };

// Export reducer by default

export default reducer;
