import React, { Component } from "react";
import PropTypes from "prop-types";
import Main from "./presenter";
class Container extends Component {

  state = {
    summary : null
  };
  static propTypes = {
    getSummary : PropTypes.func.isRequired,
  };
  
  componentWillReceiveProps = nextProps => {
    if (nextProps.summary) {
      this.setState({
        summary: nextProps.summary
      });
    }
  };
  

  componentDidMount() {
    const {getSummary} = this.props;
    console.log("Dd");
    getSummary();
    console.log(this.props);
};

  onKeyPress = (e) =>{
    this.setState({
      text : e.target.value
    })
    if(e.key==='Enter'){
      window.location.href="http://127.0.0.1:3000/summoner/"+e.target.value;
    }
  }
  onClick = (e) =>{
    const {text} = this.state;
      window.location.href="http://127.0.0.1:3000/summoner/"+text;
  }
  render() {
    
    return (
      <Main {...this.state} onKeyPress={this.onKeyPress}  onClick={this.onClick} />
      
    );
    
  }
}

export default Container;
